integrations.cloudCode:
async payload => {
// Notify dashboard users to take over
// the conversation
toolbelt.takeover()
}

async payload => {
// Send an e-mail to notify a conversation
// needs to be taken over
toolbelt.email({
  to: 'foo@bar.com',
  template: 'takeover'
})
}

async payload => {
// Disable the bot for a number of minutes
// check the brain settings
toolbelt.pauseBot()
}
